import { createStore } from 'vuex';

export default createStore({
  state: {
    user: {
      name: "Ivan_Mazepa",
      discription: "Продажа голды WOW 24/7"
    }
  },
  getters: {
    getUser (state) {
      return state.user;
    }
  },
  mutations: {
    setUser (state, newUser) {
      state.user = {
        name: newUser.name,
        discription: newUser.discription
      };
    }
  },
  actions: {
  },
  modules: {
  }
});
